(9, 1, '2014-04-07 10:52:03', '2014-04-07 10:52:03', '<strong>Current Investments</strong>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td><strong>Company</strong></td>\r\n<td><strong>Market</strong></td>\r\n<td><strong>Sector</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Lesotho Milling Company</td>\r\n<td>Lesotho</td>\r\n<td>Food</td>\r\n</tr>\r\n<tr>\r\n<td>Grassroots Group Holdings</td>\r\n<td>South Africa</td>\r\n<td>Food</td>\r\n</tr>\r\n<tr>\r\n<td>Saint Pie</td>\r\n<td>South Africa</td>\r\n<td>Food</td>\r\n</tr>\r\n<tr>\r\n<td>Gecco Holdings</td>\r\n<td>South Africa</td>\r\n<td>Waste Remediation</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<strong>Realised Investments</strong>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td><strong>Company</strong></td>\r\n<td><strong>Market</strong></td>\r\n<td><strong>Sector</strong></td>\r\n</tr>\r\n<tr>\r\n<td>Target Investments</td>\r\n<td>South Africa</td>\r\n<td>Food</td>\r\n</tr>\r\n<tr>\r\n<td>USC Metering</td>\r\n<td>South Africa</td>\r\n<td>Utilities – Water Management</td>\r\n</tr>\r\n</tbody>\r\n</table>', 'Investments', '', 'publish', 'open', 'open', '', 'investments', '', '', '2014-05-06 13:52:22', '2014-05-06 11:52:22', '', 0, 'http://localhost/~tyronestafford/acorn/?page_id=9', 0, 'page', '', 0),
