erein.', 'ESG', '', 'inherit', 'open', 'open', '', '11-revision-v1', '', '', '2014-04-10 12:45:05', '2014-04-10 12:45:05', '', 11, 'http://192.168.1.15/~tyronestafford/acorn/11-revision-v1/', 0, 'revision', '', 0),
